function googleTranslateElementInit(){new google.translate.TranslateElement({pageLanguage:"uk",includedLanguages:"uk,en,pl",autoDisplay:!1,layout:google.translate.TranslateElement.InlineLayout.SIMPLE},"select-language")}document.addEventListener("click",(e=>{const t=e.target;if(t.closest(".menu__link")){const n=t.closest(".menu__link");n.nextElementSibling&&n.nextElementSibling.classList.contains("menu__submenu")&&(e.preventDefault(),n.nextElementSibling.classList.add("-active"))}if(t.closest(".menu__back")){e.preventDefault();t.closest(".menu__back").closest(".menu__submenu").classList.remove("-active")}if(t.closest(".header__burger")){e.preventDefault();const n=t.closest(".header__burger"),l=document.querySelector(".header__decor"),s=document.querySelector(".menu");n.classList.toggle("-active"),l.classList.toggle("-active"),s.classList.toggle("-active")}}));